use Tuffnells\Models\Consignment;
use Tuffnells\Models\Package;

class ApplicationTest extends TestCase
{
    private Application $_application;

    public function setUp(): void {
        $this->_application = new Application('TEST', 'TEST', 'TEST');
        $cache = new MockCache();
        $this->_application->setCache($cache);
    }

    public function testSetCachePrefixEmpty(): void {
        $this->expectException(InvalidCache::class);
        $this->_application->setCachePrefix('');
    }

    public function testSetCachePrefix(): void {
        self::assertEquals($this->_application,$this->_application->setCachePrefix('TEST'));
    }

    private function createMockResponse(array $responses, bool $addLoginResponse = true) {
        if($addLoginResponse) {
            array_unshift($responses,
                new Response(302, ['Set-Cookie'=> 'ASP.NET_SessionId=examplesession; path=/'], '')
            );
        }

        $mock = new MockHandler($responses);
        $handlerStack = HandlerStack::create($mock);
        return new Client(['handler' => $handlerStack]);
    }

    /**
     * Creates a mock consignment
     *
     * @return Consignment
     */
    private function createMockConsignment(): Consignment {
        $consignment = new Consignment($this->_application);
        $consignment->getPackage1()->setWeight(1)->setQuantity(1);

        $address = new Address($this->_application);
        $address->setAddressLine1('1 Test Lane');
        $address->setPostcode('TT6 TT6', new CityRegion('AIRDRIE', 'LANARKSHIRE'));
        $address->setContactName('Test McTest');

        $consignment->setCollectionAddress($address);
        $consignment->setDeliveryAddress($address);

        return $consignment;
    }

    public function testLoginFailureIncorrectPassword(): void {
        $this->expectException(AccountDetailsInvalid::class);
        $this->_application->setGuzzleClient($this->createMockResponse([
            new Response(200)
        ], false));

        $this->_application->getConsignment('DOESNTEXIST');
    }

    public function testLoginFailureMissingCookie(): void {
        $this->expectException(AccountDetailsInvalid::class);
        $this->_application->setGuzzleClient($this->createMockResponse([
            new Response(302)
        ], false));

        $this->_application->getConsignment('DOESNTEXIST');
    }

    public function testLoginFailureTuffnellsFailure(): void {
        $this->expectException(EndpointError::class);
        $this->_application->setGuzzleClient($this->createMockResponse([
            new Response(500)
        ], false));

        $this->_application->getConsignment('DOESNTEXIST');
    }

    public function testGetCityRegionSuccess(): void {
        $this->_application->setGuzzleClient($this->createMockResponse([
            new Response(200, [], '<input type="hidden" name="__VIEWSTATE" value="" />'),
            new Response(302, ['Location' => '/dotweb/customeraddressentry.aspx?PostSector=ML6%209HB&CustName=&Add1=1+CAIRNDYKE+CRESCENT&Add2=&Add3=&Town=AIRDRIE&County=LANARKSHIRE']),
        ]));

        self::assertEquals('AIRDRIE', $this->_application->getCityRegion('ML6 9HB')->getCity());

        //test cache
        $this->_application->setGuzzleClient($this->createMockResponse([
            new Response(500),
        ]));

        self::assertEquals('AIRDRIE', $this->_application->getCityRegion('ML6 9HB')->getCity());
    }

    public function testGetCityRegionFailure(): void {
        $this->expectException(PostcodeNotValid::class);
        $this->_application->setGuzzleClient($this->createMockResponse([
            new Response(200, [], '<input type="hidden" name="__VIEWSTATE" value="" />'),
            new Response(200),
        ]));
        $this->_application->getCityRegion('ML6 9HB');
    }

    /**
     * @testCase Create a consignment with an invalid dispatch date
     */
    public function testCreateAmendConsignmentInvalidDate(): void {
        $this->expectException(InvalidDispatchDate::class);

        $date = new \DateTime();
        $date->modify('-1 day');
        $consignment = $this->createMockConsignment();
        $consignment->setURN('Test');
        $consignment->setConsignmentNumber('ABC');
        $consignment->setDispatchDate($date);
        $this->_application->createConsignment($consignment);
    }

    /**
     * @testCase Create a consignment but server responds wrong
     */
    public function testCreateConsignmentServerError(): void {
        $this->expectException(EndpointError::class);

        $this->_application->setGuzzleClient($this->createMockResponse([
            new Response(200, [], '<input type="hidden" name="__VIEWSTATE" value="" />'),
            new Response(200),
        ]));

        $date = new \DateTime();
        $date->modify('-1 day');
        $consignment = $this->createMockConsignment();
        $consignment->setConsignmentNumber('ABC');
        $this->_application->createConsignment($consignment);
    }

    /**
     * @testCase Create a consignment but server responds wrong
     */
    public function testAmendConsignmentInvalidConsignment(): void {
        $this->expectException(InvalidConsignment::class);

        $consignment = $this->createMockConsignment();
        $consignment->setConsignmentNumber('ABC');
        $this->_application->amendConsignment($consignment);
    }

    /**
     * @testCase Create a consignment but server responds wrong
     */
    public function testAmendConsignmentInvalidDate(): void {
        $this->expectException(InvalidDispatchDate::class);

        $date = new \DateTime();
        $date->modify('-1 day');
        $consignment = $this->createMockConsignment();
        $consignment->setConsignmentNumber('ABC');
        $consignment->setURN('1234');
        $consignment->setDispatchDate($date);
        $this->_application->amendConsignment($consignment);
    }

    /**
     * @testCase Create a consignment but server doesn't generate URN
     */
    public function testCreateConsignmentNoURNGenerated(): void {
        $this->expectException(EndpointError::class);

        $this->_application->setGuzzleClient($this->createMockResponse([
            new Response(200, [], '<input type="hidden" name="__VIEWSTATE" value="" />'),
            new Response(302,  ['Location' => '/dotweb/customeraddressentry.aspx?URNd=ABCDEFG'])
        ]));

        $date = new \DateTime();
        $date->modify('-1 day');
        $consignment = $this->createMockConsignment();
        $consignment->setConsignmentNumber('ABC');
        $this->_application->createConsignment($consignment);
    }

    /**
     * @testCase Create a consignment but server responds wrong
     */
    public function testCreateConsignmentSuccess(): void {
        $this->_application->setGuzzleClient($this->createMockResponse([
            new Response(200, [], '<input type="hidden" name="__VIEWSTATE" value="" />'),
            new Response(302,  ['Location' => '/dotweb/customeraddressentry.aspx?URN=ABCDEFG'])
        ]));

        $date = new \DateTime();
        $date->modify('-1 day');
        $consignment = $this->createMockConsignment();
        $consignment->setConsignmentNumber('ABC');
        $this->_application->createConsignment($consignment);

        self::assertEquals('ABCDEFG', $consignment->getUrn());
    }

    /**
     * @testCase Delete a consignment but server responds wrong
     */
    public function testDeleteConsignmentServerError(): void {
        $this->expectException(EndpointError::class);

        $this->_application->setGuzzleClient($this->createMockResponse([
            new Response(200, [], '<input type="hidden" name="__VIEWSTATE" value="" />'),
            new Response(200)
        ]));

        $consignment = $this->createMockConsignment();
        $consignment->setURN('1234');
        $this->_application->deleteConsignment($consignment);
    }

    /**
     * @testCase Create a consignment but server responds wrong
     */
    public function testDeleteConsignment(): void {
        $this->_application->setGuzzleClient($this->createMockResponse([
            new Response(200, [], '<input type="hidden" name="__VIEWSTATE" value="" />'),
            new Response(302,  ['Location' => '/dotweb/customeraddressentry.aspx?URN=ABCDEFG'])
        ]));

        $consignment = $this->createMockConsignment();
        $consignment->setURN('1234');
        $this->assertTrue($this->_application->deleteConsignment($consignment));
    }

    /**
     * @testCase Tries getting a consignment without a valid URN
     * @throws Exceptions\ConsignmentNotFound
     */
    public function testGetConsignmentEmptyURN(): void {
        $this->expectException(InvalidURN::class);
        $this->_application->getConsignment('');
